// Three.js 3D Ocean Scene with Water Shader
let scene, camera, renderer;
let waterMesh;
let time = 0;

function init() {
  // Scene setup
  scene = new THREE.Scene();
  scene.background = new THREE.Color(0x1a1a2e);
  scene.fog = new THREE.Fog(0x1a1a2e, 100, 500);

  // Camera setup
  const width = window.innerWidth;
  const height = window.innerHeight;
  camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
  camera.position.set(0, 15, 30);
  camera.lookAt(0, 0, 0);

  // Renderer setup
  const canvas = document.getElementById('canvas');
  renderer = new THREE.WebGLRenderer({
    canvas: canvas,
    antialias: true,
    alpha: true
  });
  renderer.setSize(width, height);
  renderer.setPixelRatio(window.devicePixelRatio);
  renderer.shadowMap.enabled = true;
  renderer.shadowMap.type = THREE.PCFShadowShadowMap;

  // Lighting
  const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
  scene.add(ambientLight);

  const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
  directionalLight.position.set(50, 50, 50);
  directionalLight.castShadow = true;
  directionalLight.shadow.mapSize.width = 2048;
  directionalLight.shadow.mapSize.height = 2048;
  directionalLight.shadow.camera.far = 200;
  directionalLight.shadow.camera.left = -100;
  directionalLight.shadow.camera.right = 100;
  directionalLight.shadow.camera.top = 100;
  directionalLight.shadow.camera.bottom = -100;
  scene.add(directionalLight);

  // Add sky gradient
  const skyGeometry = new THREE.SphereGeometry(400, 32, 32);
  const skyShader = createSkyShader();
  const skyMaterial = new THREE.ShaderMaterial({
    uniforms: skyShader.uniforms,
    vertexShader: skyShader.vertexShader,
    fragmentShader: skyShader.fragmentShader,
    side: THREE.BackSide
  });
  const skyMesh = new THREE.Mesh(skyGeometry, skyMaterial);
  scene.add(skyMesh);

  // Create water surface
  createWater();

  // Add distant islands/landscape hint
  addLandscape();

  // Handle window resize
  window.addEventListener('resize', onWindowResize, false);

  // Start animation loop
  animate();
}

function createWater() {
  const waterGeometry = new THREE.PlaneGeometry(500, 500, 256, 256);
  waterGeometry.rotateX(-Math.PI / 2);

  const waterShader = createWaterShader();

  const waterMaterial = new THREE.ShaderMaterial({
    uniforms: waterShader.uniforms,
    vertexShader: waterShader.vertexShader,
    fragmentShader: waterShader.fragmentShader,
    side: THREE.DoubleSide,
    wireframe: false
  });

  waterMesh = new THREE.Mesh(waterGeometry, waterMaterial);
  waterMesh.receiveShadow = true;
  scene.add(waterMesh);
}

function createWaterShader() {
  return {
    uniforms: {
      time: { value: 0.0 },
      waterColor: { value: new THREE.Color(0x006994) },
      deepColor: { value: new THREE.Color(0x001a33) },
      foamColor: { value: new THREE.Color(0xffffff) }
    },
    vertexShader: `
      uniform float time;
      varying vec3 vPosition;
      varying vec3 vNormal;
      varying float vWave;

      // Simplex-like noise
      float noise(vec3 p) {
        return sin(p.x * 0.1) * cos(p.z * 0.1) * sin(time * 0.5);
      }

      float gerstnerWave(vec4 wave, vec3 p) {
        float steepness = wave.z;
        float wavelength = wave.w;
        float k = 2.0 * 3.14159 / wavelength;
        float c = sqrt(9.8 / k);
        vec2 d = normalize(wave.xy);
        float f = k * (dot(d, p.xz) - c * time * 0.5);
        float a = steepness / k;

        return a * sin(f);
      }

      void main() {
        vPosition = position;
        vec3 p = position;

        // Apply multiple waves for realistic water
        float wave1 = gerstnerWave(vec4(1.0, 0.0, 0.25, 60.0), p);
        float wave2 = gerstnerWave(vec4(0.2, 0.4, 0.15, 31.0), p);
        float wave3 = gerstnerWave(vec4(0.5, 0.1, 0.1, 18.0), p);

        p.y += wave1 + wave2 + wave3;
        p.y += sin(time * 0.3 + p.x * 0.01) * 0.3;

        vWave = wave1 + wave2 + wave3;
        vNormal = normalize(normal);

        gl_Position = projectionMatrix * modelViewMatrix * vec4(p, 1.0);
      }
    `,
    fragmentShader: `
      uniform vec3 waterColor;
      uniform vec3 deepColor;
      uniform vec3 foamColor;
      uniform float time;
      varying vec3 vPosition;
      varying vec3 vNormal;
      varying float vWave;

      void main() {
        vec3 color = mix(deepColor, waterColor, 0.5 + vWave * 0.3);

        // Add foam where waves peak
        float foam = smoothstep(0.3, 0.5, vWave);
        color = mix(color, foamColor, foam * 0.8);

        // Add specular highlights
        vec3 viewDir = normalize(cameraPosition - vPosition);
        float spec = pow(max(dot(vNormal, viewDir), 0.0), 32.0);
        color += vec3(1.0) * spec * 0.5;

        // Add some shimmer
        color += sin(vPosition.x * 0.1 + time) * 0.05;

        gl_FragColor = vec4(color, 0.95);
      }
    `
  };
}

function createSkyShader() {
  return {
    uniforms: {
      time: { value: 0.0 }
    },
    vertexShader: `
      varying vec3 vPosition;

      void main() {
        vPosition = normalize(position);
        gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
      }
    `,
    fragmentShader: `
      varying vec3 vPosition;
      uniform float time;

      void main() {
        // Create gradient sky
        float y = normalize(vPosition).y;

        // Sunset/sunrise colors
        vec3 topColor = mix(
          vec3(0.1, 0.2, 0.5),  // Dark blue
          vec3(1.0, 0.7, 0.3),  // Orange/sunset
          sin(y + sin(y) * 0.3) * 0.5 + 0.5
        );

        // Horizon
        vec3 horizonColor = vec3(1.0, 0.6, 0.2);

        // Mix based on position
        vec3 color = mix(horizonColor, topColor, abs(y));

        // Add some clouds effect
        color += sin(vPosition.x * 2.0) * cos(vPosition.z * 2.0) * 0.05;

        gl_FragColor = vec4(color, 1.0);
      }
    `
  };
}

function addLandscape() {
  // Add a distant mountain/island silhouette
  const mountainGeometry = new THREE.ConeGeometry(50, 80, 32);
  const mountainMaterial = new THREE.MeshStandardMaterial({
    color: 0x2a4a3a,
    roughness: 0.8,
    metalness: 0.1
  });

  const mountain1 = new THREE.Mesh(mountainGeometry, mountainMaterial);
  mountain1.position.set(-150, 0, -200);
  mountain1.castShadow = true;
  mountain1.receiveShadow = true;
  scene.add(mountain1);

  const mountain2 = new THREE.Mesh(mountainGeometry, mountainMaterial);
  mountain2.position.set(150, 0, -250);
  mountain2.scale.set(0.8, 0.8, 0.8);
  mountain2.castShadow = true;
  mountain2.receiveShadow = true;
  scene.add(mountain2);
}

function animate() {
  requestAnimationFrame(animate);

  time += 0.01;

  // Update water shader time
  if (waterMesh && waterMesh.material.uniforms) {
    waterMesh.material.uniforms.time.value = time;
  }

  // Gentle camera movement
  const cameraX = Math.sin(time * 0.1) * 20;
  camera.position.x = cameraX;
  camera.lookAt(0, 5, 0);

  renderer.render(scene, camera);
}

function onWindowResize() {
  const width = window.innerWidth;
  const height = window.innerHeight;

  camera.aspect = width / height;
  camera.updateProjectionMatrix();

  renderer.setSize(width, height);
}

// Initialize when DOM is ready
if (document.readyState === 'loading') {
  document.addEventListener('DOMContentLoaded', init);
} else {
  init();
}

// Three.js 3D Ocean Scene with Water Shader
let scene, camera, renderer;
let waterMesh;
let time = 0;

function init() {
  // Scene setup
  scene = new THREE.Scene();
  scene.background = new THREE.Color(0x1a1a2e);
  scene.fog = new THREE.Fog(0x1a1a2e, 100, 500);

  // Camera setup
  const width = window.innerWidth;
  const height = window.innerHeight;
  camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
  camera.position.set(0, 15, 30);
  camera.lookAt(0, 0, 0);

  // Renderer setup
  const canvas = document.getElementById('canvas');
  renderer = new THREE.WebGLRenderer({
    canvas: canvas,
    antialias: true,
    alpha: true
  });
  renderer.setSize(width, height);
  renderer.setPixelRatio(window.devicePixelRatio);
  renderer.shadowMap.enabled = true;
  renderer.shadowMap.type = THREE.PCFShadowShadowMap;

  // Lighting
  const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
  scene.add(ambientLight);

  const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
  directionalLight.position.set(50, 50, 50);
  directionalLight.castShadow = true;
  directionalLight.shadow.mapSize.width = 2048;
  directionalLight.shadow.mapSize.height = 2048;
  directionalLight.shadow.camera.far = 200;
  directionalLight.shadow.camera.left = -100;
  directionalLight.shadow.camera.right = 100;
  directionalLight.shadow.camera.top = 100;
  directionalLight.shadow.camera.bottom = -100;
  scene.add(directionalLight);

  // Add sky gradient
  const skyGeometry = new THREE.SphereGeometry(400, 32, 32);
  const skyShader = createSkyShader();
  const skyMaterial = new THREE.ShaderMaterial({
    uniforms: skyShader.uniforms,
    vertexShader: skyShader.vertexShader,
    fragmentShader: skyShader.fragmentShader,
    side: THREE.BackSide
  });
  const skyMesh = new THREE.Mesh(skyGeometry, skyMaterial);
  scene.add(skyMesh);

  // Create water surface
  createWater();

  // Add distant islands/landscape hint
  addLandscape();

  // Handle window resize
  window.addEventListener('resize', onWindowResize, false);

  // Start animation loop
  animate();
}

function createWater() {
  const waterGeometry = new THREE.PlaneGeometry(500, 500, 256, 256);
  waterGeometry.rotateX(-Math.PI / 2);

  const waterShader = createWaterShader();

  const waterMaterial = new THREE.ShaderMaterial({
    uniforms: waterShader.uniforms,
    vertexShader: waterShader.vertexShader,
    fragmentShader: waterShader.fragmentShader,
    side: THREE.DoubleSide,
    wireframe: false
  });

  waterMesh = new THREE.Mesh(waterGeometry, waterMaterial);
  waterMesh.receiveShadow = true;
  scene.add(waterMesh);
}

function createWaterShader() {
  return {
    uniforms: {
      time: { value: 0.0 },
      waterColor: { value: new THREE.Color(0x006994) },
      deepColor: { value: new THREE.Color(0x001a33) },
      foamColor: { value: new THREE.Color(0xffffff) }
    },
    vertexShader: `
      uniform float time;
      varying vec3 vPosition;
      varying vec3 vNormal;
      varying float vWave;
      varying vec3 vViewDir;

      // Improved noise function
      float noise3D(vec3 p) {
        vec3 i = floor(p);
        vec3 f = fract(p);
        f = f * f * (3.0 - 2.0 * f);

        float n = mix(
          mix(sin(i.x + i.y + i.z), cos(i.x - i.y), f.x),
          mix(sin(i.y - i.z), cos(i.x + i.z), f.x),
          f.y
        );
        return mix(n, sin(p.x * 0.1 + time * 0.3), f.z);
      }

      float gerstnerWave(vec4 wave, vec3 p) {
        float steepness = wave.z;
        float wavelength = wave.w;
        float k = 2.0 * 3.14159 / wavelength;
        float c = sqrt(9.8 / k);
        vec2 d = normalize(wave.xy);
        float f = k * (dot(d, p.xz) - c * time * 0.5);
        float a = steepness / k;

        return a * sin(f);
      }

      void main() {
        vPosition = position;
        vec3 p = position;

        // Apply multiple waves for realistic water
        float wave1 = gerstnerWave(vec4(1.0, 0.0, 0.35, 60.0), p);
        float wave2 = gerstnerWave(vec4(0.2, 0.4, 0.25, 31.0), p);
        float wave3 = gerstnerWave(vec4(0.5, 0.1, 0.15, 18.0), p);
        float wave4 = gerstnerWave(vec4(-0.3, 0.6, 0.1, 12.0), p);

        // Combine waves
        p.y += wave1 + wave2 + wave3 + wave4;

        // Add additional height variation with noise
        p.y += noise3D(p * 0.5) * 0.4;
        p.y += sin(time * 0.2 + p.x * 0.02) * 0.2;

        vWave = wave1 + wave2 + wave3 + wave4;
        vNormal = normalize(normal);
        vViewDir = normalize(cameraPosition - vPosition);

        gl_Position = projectionMatrix * modelViewMatrix * vec4(p, 1.0);
      }
    `,
    fragmentShader: `
      uniform vec3 waterColor;
      uniform vec3 deepColor;
      uniform vec3 foamColor;
      uniform float time;
      varying vec3 vPosition;
      varying vec3 vNormal;
      varying float vWave;
      varying vec3 vViewDir;

      // Fresnel effect for realistic water
      float fresnel(vec3 viewDir, vec3 normal) {
        float cosTheta = max(dot(viewDir, normal), 0.0);
        return pow(1.0 - cosTheta, 5.0);
      }

      void main() {
        // Base water color with depth
        vec3 color = mix(deepColor, waterColor, 0.6 + vWave * 0.4);

        // Add foam where waves peak (improved)
        float foam = smoothstep(0.2, 0.6, vWave);
        foam = mix(foam, 0.0, mod(vWave * 3.0 + time, 1.0)); // animated foam pattern
        color = mix(color, foamColor, foam * 0.9);

        // Enhanced specular highlights
        vec3 viewDir = normalize(cameraPosition - vPosition);
        float spec = pow(max(dot(vNormal, viewDir), 0.0), 64.0);
        color += vec3(1.0) * spec * 0.8;

        // Add fresnel effect (water looks more reflective at grazing angles)
        float fresnel = fresnel(viewDir, vNormal);
        color = mix(color, vec3(1.0) * 0.5, fresnel * 0.3);

        // Shimmer and glitter effects
        float shimmer = sin(vPosition.x * 0.15 + time * 0.4) * 0.5 + 0.5;
        shimmer *= cos(vPosition.z * 0.12 + time * 0.3) * 0.5 + 0.5;
        color += vec3(0.8, 0.9, 1.0) * shimmer * 0.15;

        // Add underwater caustics effect
        float caustic = sin(vPosition.x * 0.08 + time * 0.2) * 
                       cos(vPosition.z * 0.08 + time * 0.25);
        color += vec3(0.1, 0.2, 0.3) * caustic * 0.1;

        // Brighten wave peaks
        float brightness = smoothstep(0.1, 0.5, vWave);
        color += vec3(0.1) * brightness;

        gl_FragColor = vec4(color, 0.95);
      }
    `
  };
}

function createSkyShader() {
  return {
    uniforms: {
      time: { value: 0.0 }
    },
    vertexShader: `
      varying vec3 vPosition;

      void main() {
        vPosition = normalize(position);
        gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
      }
    `,
    fragmentShader: `
      varying vec3 vPosition;
      uniform float time;

      void main() {
        // Create gradient sky
        float y = normalize(vPosition).y;

        // Sunset/sunrise colors
        vec3 topColor = mix(
          vec3(0.1, 0.2, 0.5),  // Dark blue
          vec3(1.0, 0.7, 0.3),  // Orange/sunset
          sin(y + sin(y) * 0.3) * 0.5 + 0.5
        );

        // Horizon
        vec3 horizonColor = vec3(1.0, 0.6, 0.2);

        // Mix based on position
        vec3 color = mix(horizonColor, topColor, abs(y));

        // Add some clouds effect
        color += sin(vPosition.x * 2.0) * cos(vPosition.z * 2.0) * 0.05;

        gl_FragColor = vec4(color, 1.0);
      }
    `
  };
}

function addLandscape() {
  // Add a distant mountain/island silhouette
  const mountainGeometry = new THREE.ConeGeometry(50, 80, 32);
  const mountainMaterial = new THREE.MeshStandardMaterial({
    color: 0x2a4a3a,
    roughness: 0.8,
